import {extend} from '@keystonehq/bc-ur-registry'
const {RegistryType} = extend;

export const ExtendedRegistryTypes = {
    ETH_SIGN_REQUEST: new RegistryType('eth-sign-request', 401),
    ETH_SIGNATAURE: new RegistryType('eth-signature', 402)
}

import { CryptoKeypath, extend, DataItem, PathComponent, RegistryItem } from '@keystonehq/bc-ur-registry';
import { ExtendedRegistryTypes } from './RegistryType';
import * as uuid from 'uuid';

const { decodeToDataItem, RegistryTypes } = extend;

enum Keys {
    requestId = 1,
    signData,
    dataType,
    chainId,
    derivationPath,
    address,
}

export enum DataType {
    transaction = 1,
    typedData = 2,
    personalMessage = 3,
    typedTransaction = 4,
}

type signRequestProps = {
    requestId?: Buffer;
    signData: Buffer;
    dataType: DataType;
    chainId?: number;
    derivationPath: CryptoKeypath;
    address?: Buffer;
};

export class EthSignRequest extends RegistryItem {
    private requestId: Buffer;
    private signData: Buffer;
    private dataType: DataType;
    private chainId: number;
    private derivationPath: CryptoKeypath;
    private address: Buffer;

    getRegistryType = () => ExtendedRegistryTypes.ETH_SIGN_REQUEST;

    constructor(args: signRequestProps) {
        super();
        this.setupData(args);
    }

    private setupData = (args: signRequestProps) => {
        this.requestId = args.requestId;
        this.signData = args.signData;
        this.dataType = args.dataType;
        this.chainId = args.chainId;
        this.derivationPath = args.derivationPath;
        this.address = args.address;
    };

    public getRequestId = () => this.requestId;
    public getSignData = () => this.signData;
    public getDataType = () => this.dataType;
    public getChainId = () => this.chainId;
    public getDerivationPath = () => this.derivationPath.getPath();
    public getSignRequestAddress = () => this.address;

    public toDataItem = () => {
        const map = {};
        if (this.requestId) {
            map[Keys.requestId] = new DataItem(this.requestId, RegistryTypes.UUID.getTag());
        }
        if (this.address) {
            map[Keys.address] = this.address;
        }
        if (this.chainId) {
            map[Keys.chainId] = this.chainId;
        }

        map[Keys.signData] = this.signData;
        map[Keys.dataType] = this.dataType;

        const keyPath = this.derivationPath.toDataItem();
        keyPath.setTag(this.derivationPath.getRegistryType().getTag());
        map[Keys.derivationPath] = keyPath;

        return new DataItem(map);
    };

    public static fromDataItem = (dataItem) => {
        const map = dataItem.getData();
        const signData = map[Keys.signData];
        const dataType = map[Keys.dataType];
        const derivationPath = CryptoKeypath.fromDataItem(map[Keys.derivationPath]);
        const chainId = map[Keys.chainId] ? map[Keys.chainId] : undefined;
        const address = map[Keys.address] ? map[Keys.address] : undefined;
        const requestId = map[Keys.requestId] ? map[Keys.requestId].getData() : undefined;

        return new EthSignRequest({
            requestId,
            signData,
            dataType,
            chainId,
            derivationPath,
            address,
        });
    };

    public static fromCBOR = (_cborPayload: Buffer) => {
        const dataItem = decodeToDataItem(_cborPayload);
        return EthSignRequest.fromDataItem(dataItem);
    };

    public static constructETHRequest(
        signData: Buffer,
        signDataType: DataType,
        hdPath: string,
        xfp: string,
        uuidString?: string,
        chainId?: number,
        address?: string,
    ) {
        const paths = hdPath.replace(/[m|M]\//, '').split('/');
        const hdpathObject = new CryptoKeypath(
            paths.map((path) => {
                const index = parseInt(path.replace("'", ''));
                let isHardened = false;
                if (path.endsWith("'")) {
                    isHardened = true;
                }
                return new PathComponent({ index, hardened: isHardened });
            }),
            Buffer.from(xfp, 'hex'),
        );

        return new EthSignRequest({
            requestId: uuidString ? Buffer.from(uuid.parse(uuidString) as Uint8Array) : undefined,
            signData,
            dataType: signDataType,
            derivationPath: hdpathObject,
            chainId,
            address: address ? Buffer.from(address.replace('0x', ''), 'hex') : undefined,
        });
    }
}

import { extend, DataItem, RegistryItem } from '@keystonehq/bc-ur-registry';
import { ExtendedRegistryTypes } from './RegistryType';

const { RegistryTypes, decodeToDataItem } = extend;

enum Keys {
    requestId = 1,
    signature,
}

export class ETHSignature extends RegistryItem {
    private requestId: Buffer;
    private signature: Buffer;

    getRegistryType = () => ExtendedRegistryTypes.ETH_SIGNATAURE;

    constructor(signature: Buffer, requestId?: Buffer) {
        super();
        this.signature = signature;
        this.requestId = requestId;
    }

    public getRequestId = () => this.requestId;
    public getSignature = () => this.signature;

    public toDataItem = () => {
        const map = {};
        if (this.requestId) {
            map[Keys.requestId] = new DataItem(this.requestId, RegistryTypes.UUID.getTag());
        }
        map[Keys.signature] = this.signature;
        return new DataItem(map);
    };

    public static fromDataItem = (dataItem) => {
        const map = dataItem.getData();
        const signature = map[Keys.signature];
        const requestId = map[Keys.requestId] ? map[Keys.requestId].getData() : undefined;

        return new ETHSignature(signature, requestId);
    };

    public static fromCBOR = (_cborPayload: Buffer) => {
        const dataItem = decodeToDataItem(_cborPayload);
        return ETHSignature.fromDataItem(dataItem);
    };
}

// @ts-ignore
import HDKey from 'hdkey'
import { toChecksumAddress, publicToAddress } from 'ethereumjs-util';

export const generateAddressfromXpub = (xpub: string, derivePath: string) => {
    // @ts-ignore
    const node = HDKey.fromExtendedKey(xpub)
    const publicKey = node.derive(derivePath)
    const address = '0x' + publicToAddress(publicKey.publicKey, true).toString('hex');
    return toChecksumAddress(address);
}


export const findHDpatfromAddress = (address: string, xpub: string, numberLimit: number, rootPath: string) => {
    for (let i = 0; i < numberLimit; i++) {
        let path = `M/0/${i}`
        let caculateAddress = generateAddressfromXpub(xpub, path)
        if (address.toLowerCase() == caculateAddress.toLowerCase()) {
            return `${rootPath}/0/${i}`
        }
    }
    return null
}

export * from '@keystonehq/bc-ur-registry';
import { extend } from '@keystonehq/bc-ur-registry';
import { ExtendedRegistryTypes } from './RegistryType';
const { cbor } = extend;
cbor.patchTags(
    Object.values(ExtendedRegistryTypes)
        .filter((rt) => !!rt.getTag())
        .map((rt) => rt.getTag()),
);

export { EthSignRequest, DataType } from './EthSignRequest';
export { ETHSignature } from './EthSignature';

export { generateAddressfromXpub, findHDpatfromAddress } from './utlis'
