            {
                to: txJson['to'],
                gasLimit: txJson['gasLimit'],
                gasPrice: txJson['gasPrice'],
                data: txJson['data'],
                nonce: txJson['nonce'],
                value: txJson['value'],
                r,
                s,
                v,
            },
            { common: tx.common },
